def mount_adls_container(adlsContainer, mountPath, adlsLinkedService):
    try:
        mssparkutils.fs.mount(
                                 adlsContainer, 
                                 mountPath, 
                                 {"LinkedService": f"{adlsLinkedService}"}
            )
    except:
            print(f"{mountPath} already mounted.")

def get_data_catalog_json_schema(jobid, datagovernance_mount_path, data_catalog):
    data_catalog_path = f"synfs:/{jobid}/{datagovernance_mount}/{data_catalog}"
    return mssparkutils.fs.head(data_catalog_path)

def prepare_get_datasource(dname:str = 'DataFrame_Tenant_Source') ->:
    ge_dataframe_datasource = {
        "name" : dname,
        "class_name" : "Datasource",
        "execution_engine" : {"class_name": "SparkDFExecutionEngine", "module_name": "great_expectation_engine",},
        "data_connectors" : {
            "DataFrame_Tenant_Data_Connector":{
            "module_name": "great_expectatations.datasource.data_connector",
            "class_name": "RuntimeDataConnector",
            "batch_spec_passthrough": {
                "reader_method": "delta",
                "reader_options": {
                    "header": True,
                    "inferSchema": True,
                },
            },
            "batch_identifienrs": {
                "tenantrs_source"
            },

            }
            
        },
    }
    return ge_dataframe_datasource

